module.exports=function(a){if("number"==typeof a){return convert(a)}var d=a.match(/^(\d+(?:\.\d+)?) *(kb|mb|gb|tb)$/),e=parseFloat(d[1]),b=d[2];var c={kb:1<<10,mb:1<<20,gb:1<<30,tb:((1<<30)*1024)};return c[b]*e};function convert(a){var c=((1<<30)*1024),e=1<<30,f=1<<20,d=1<<10;if(a>=c){return(Math.round(a/c*100)/100)+"tb"}if(a>=e){return(Math.round(a/e*100)/100)+"gb"}if(a>=f){return(Math.round(a/f*100)/100)+"mb"}if(a>=d){return(Math.round(a/d*100)/100)+"kb"}return a+"b"};