function accepts(a){return require("../")({headers:{"accept-encoding":a||""}})}describe("accepts.encodings()",function(){describe("with no arguments",function(){describe("when Accept-Encoding is populated",function(){it("should return accepted types",function(){var a=accepts("gzip, compress;q=0.2");a.encodings().should.eql(["gzip","compress","identity"]);a.encodings("gzip","compress").should.equal("gzip")})});describe("when Accept-Encoding is not populated",function(){it("should return identity",function(){var a=accepts();a.encodings().should.eql(["identity"]);a.encodings("gzip","deflate").should.equal("identity")})})});describe("with multiple arguments",function(){it("should return the best fit",function(){var a=accepts("gzip, compress;q=0.2");a.encodings("compress","gzip").should.eql("gzip");a.encodings("gzip","compress").should.eql("gzip")})});describe("with an array",function(){it("should return the best fit",function(){var a=accepts("gzip, compress;q=0.2");a.encodings(["compress","gzip"]).should.eql("gzip")})})});