var crc32=require("..");var test=require("tap").test;test("simple crc32 is no problem",function(b){var a=Buffer("hey sup bros");var c=Buffer([71,250,85,112]);b.same(crc32(a),c);b.end()});test("another simple one",function(b){var a=Buffer("IEND");var c=Buffer([174,66,96,130]);b.same(crc32(a),c);b.end()});test("slightly more complex",function(b){var a=Buffer([0,0,0]);var c=Buffer([255,65,217,18]);b.same(crc32(a),c);b.end()});test("complex crc32 gets calculated like a champ",function(b){var a=Buffer("शीर्षक");var c=Buffer([23,184,175,241]);b.same(crc32(a),c);b.end()});test("casts to buffer if necessary",function(b){var a="शीर्षक";var c=Buffer([23,184,175,241]);b.same(crc32(a),c);b.end()});test("can do signed",function(b){var a="ham sandwich";var c=-1891873021;b.same(crc32.signed(a),c);b.end()});test("can do unsigned",function(b){var a="bear sandwich";var c=3711466352;b.same(crc32.unsigned(a),c);b.end()});test("simple crc32 in append mode",function(c){var a=[Buffer("hey"),Buffer(" "),Buffer("sup"),Buffer(" "),Buffer("bros")];var d=Buffer([71,250,85,112]);for(var e=0,b=0;b<a.length;b++){e=crc32(a[b],e)}c.same(e,d);c.end()});test("can do signed in append mode",function(c){var b="ham";var a=" ";var f="sandwich";var d=-1891873021;var e=crc32.signed(b);e=crc32.signed(a,e);e=crc32.signed(f,e);c.same(e,d);c.end()});test("can do unsigned in append mode",function(c){var b="bear san";var a="dwich";var d=3711466352;var e=crc32.unsigned(b);e=crc32.unsigned(a,e);c.same(e,d);c.end()});